import React from "react";
import { Text, Dimensions, TouchableHighlight, Button } from "react-native";
import { View } from "native-base";
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
const { width } = Dimensions.get("window");

export const Fare = ({ fare, distanceMatrix, onPressAction }) => {
    return (
        <View style={styles.fareContainer}>
            <View style={{ borderBottomWidth: 1, marginBottom: 10, borderBottomColor: '1px solid black', justifyContent: "center", alignItems: "center" }}>
                <Text style={styles.amount}>Rs.{fare}</Text>
                <View><Text style={styles.fareText}>Total Fare</Text></View>
            </View>
            <View style={styles.container}>
                <View style={{ flex: 60, borderRightColor: '1px solid black', borderRightWidth: 1 }}>
                    <Text>Estimated Distance: {distanceMatrix.travelDistance}</Text>
                    <Text>Estimated Time: {distanceMatrix.travelDuration} </Text>
                </View>
                <View style={{ flex: 60, flexDirection: 'row', justifyContent: "center", alignItems: "center" }}>
                    <Icon name="cash" color={'black'} size={25} />
                    <Text>CASH</Text>
                </View>
            </View>
            <TouchableHighlight style={styles.btn} onPress={onPressAction}>
                <Text>Request Taxi</Text>
            </TouchableHighlight>
        </View>

    );
}

export default Fare;

const styles = {
    fareContainer: {
        width: width,
        height: 175,
        padding: 10,
        backgroundColor: "white"
    },
    fareText: {
        fontSize: 12,
        marginBottom: 10

    },
    amount: {
        fontWeight: "bold",
        fontSize: 16
    },
    btn: {
        width: '100%',
        height: 40,
        justifyContent: 'center',
        alignItems: 'center',
        borderWidth: 1,
        marginTop: 15,
        backgroundColor: "#1abc9c",
        borderColor: '#009387',
        border: '1px solid transparent'
        // backgroundImage: linear-gradient('141deg', '#9fb8ad 0%', '#1fc8db 51%', '#2cb5e8 75%')
    },
    container: {
        flexDirection: 'row',
        marginBottom: 5
    }
};
